void mainImage(out vec4 O, vec2 u) {
    vec2 U = u+u - iResolution.xy;
    float T = 6.2832, l = length(U) / 30.,
          L = exp2(floor(log2(l))) * 9.,
          a = atan(U.x,U.y) - iTime *floor(l-5.)/2.;
    O = .6 + .4* cos( floor(l)+floor(fract(a/T)*L) + vec4(0,23,21,0) )
        - max(0., 9.* max( cos( T*l ), cos( a*L  )) - 8. ); }
