// Binary Tree, Smooth with (almost) fix line-size - written 2015 by Jakob Thomsen
// Thanks to FabriceNeyret2 and 834144373 for optimizations!

void mainImage( out vec4 o, in vec2 U )
//void mainImage( inout vec4 o, vec2 U ) // ERROR: overloaded functions must have the same parameter qualifiers

{
    U /= iResolution.xy;
    float y = (1.-U.y);
    U.x *= exp2(ceil(U.y *= 5.))/2.;
    U = fract(U);
    o -= o;
    //o += 1. - smoothstep(0., (.04 - .02 * U.y)/y, abs(4. * abs(U.x - .5) - U.y)); // straight lines
    o += 1. - smoothstep(0., (.04 - .02 * U.y)/y, abs(4. * abs(U.x - .5) - 1. - log2(.5 * (U.y + 1.0)))); // smooth
}
