void mainImage( out vec4 color, in vec2 pixCoords )
{
	float circleX=iResolution.x/2.0;
    float circleY=iResolution.y/2.0;
    float deltaX=pixCoords.x-circleX;
    float deltaY=pixCoords.y-circleY;
    float dist = sqrt (deltaX*deltaX+deltaY*deltaY);
    float light = 0.5*cos(dist*0.5-iTime*80.0)+0.5;



    color=vec4(light,light,light,1.0);





}
