void mainImage( out vec4 fragColor, in vec2 fragCoord ){

    vec2 uv=(fragCoord.xy*2.-iResolution.xy)/iResolution.y ;

    if(abs(uv.x)<.95&&abs(uv.y)<.95){ 

    uv*=12.0;
    uv.y+=8.5;

    // ^;..;^
    float bat;

    const float n =12.0;
    const float wingspeed =2.;

        bat=sqrt( (n*n)+(uv.x*uv.x-12.)*-(cos(iTime*3.)*wingspeed+.5) ),
    	bat*=.2/(abs(uv.x-cos(iTime) )+uv.y-bat)*.05;

    vec4 col=vec4(0.);
    (mod(uv.x*uv.x-fract(uv.x-iTime)/2.,2.0)<bat)?
        col =vec4(1.,0.,0.,1.0)/2.
		:col = 1.5/length(vec2(uv.x,uv.y+tan(iTime*.1)))-vec4(1.0,.0,.0,1.0)-bat;

   if(bat+.1>cos(iTime*18.)*.5-.5 )
       fragColor =col*vec4(.8,.5,.5,1.);
    }

    else  fragColor =vec4(1.);
 }
