void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = texture(iChannel0, uv);
}

vec2 poltocar( vec2 pol ) {
    return vec2( pol.x * cos(pol.y), pol.x * sin(pol.y) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord / iResolution.st;
    vec2 displacedUv = uv;
    vec4 imageIn = texture(iChannel1, uv);    
    vec4 feedbackIn = texture(iChannel0, uv);

    displacedUv += poltocar( vec2(length(feedbackIn.rg), length(feedbackIn.rb)) ) / 1000.0;
    vec4 feedback = texture(iChannel0, displacedUv);
    feedback.r = mod(feedback.r + 0.001, 1.0);
    feedback.b = mod(feedback.b + (feedback.r / 10.0), 1.0);

    if(iFrame <= 10 || iMouse.z > 0.0){
        fragColor = imageIn;
    } else {
        fragColor = feedback;
    }
}
