void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

   vec4 color = vec4(0.0,0.0,0.0,0.0);

    if (uv.x > 0.5&& uv.y > 0.5) //右上
    {
   		color = texture(iChannel0, vec2(1.0 - (uv.x/0.5), uv.y/0.5));
    } else if (uv.x > 0.5) { // 右下
        color = 1.0 - texture(iChannel0, vec2(1.0 - (uv.x/0.5), uv.y/0.5));
    } else if (uv.y > 0.5) { //左上
    	color =  texture(iChannel0, vec2(uv.x/0.5, uv.y/0.5));
    } else { //左下
    	color = 1.0 - texture(iChannel0, vec2(uv.x/0.5, uv.y/0.5));
    }
	fragColor = color;
}
