vec4 indicator(float t, float innerRadius, float outerRadius, in vec2 pos, in vec4 baseColor, in vec4 progressColor)
{
    const float pi = 3.14159254;
    const float twoPi = 2.0 * pi;

    vec2 uv = pos / outerRadius;
    vec2 uvNormalized = (vec2(-2.0) * uv + vec2(1.0));
    float l = length(outerRadius * uvNormalized);

    float uvAngle = atan(uvNormalized.x, uvNormalized.y) + pi;
    float interpAngle = t * twoPi;

    vec4 c0 = baseColor * step(interpAngle, uvAngle);
    vec4 c1 = progressColor * step(uvAngle, interpAngle);

    float amInRing = step(l, outerRadius) * step(innerRadius, l);
    return (c0+c1) * amInRing;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 wh = vec2(iResolution.xy);
    vec2 aspect = vec2(min(wh.x, wh.y) / max(wh.x, wh.y), 1.0);
    wh *= aspect;

    vec2 offset = fragCoord / wh;

    float percent0 = mod(iTime*0.25, 1.0);
    float percent1 = mod(iTime, 1.0);
    float percent2 = mod(iTime*0.33333, 1.0);
    vec4 baseColor0 = vec4(0.5, 0.5, 0.5, 1.0);
    vec4 baseColor1 = vec4(0.48, 0.35, 0.5, 1.0);

	fragColor =
        indicator(percent0, 0.45, 0.5, offset-vec2(0.1, 0.1), baseColor0, vec4(0.0, 0.675, 0.62, 1.0)) +
        indicator(percent1, 0.1, 0.25, offset-vec2(0.6, 0.6), baseColor0, vec4(1.0, 0.475, 0.42, 1.0)) +
        indicator(percent2, 0.0, 0.5,  offset-vec2(1.0, 0.3), baseColor1, vec4(0.75, 0.75, 0.75, 1.0));
}
