// gigatron (Ronan) France .. bob code from glslsandbox !


vec3 bob(vec2 p, vec2 t, float r, float i) {
	if (length(t-p) < r) {
		return vec3(0.1, 0.4, length(p*sqrt(p*p)));
	}
	return vec3(0.0);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv= fragCoord.xy / iResolution.xy*2.-1.;
    vec3 tx=texture(iChannel0,uv).xyz;

    uv =1.-uv*abs(sin(iTime))*4.;// t2 screen bounce

    vec3 tx2=texture(iChannel2,uv).xyz;
	vec2 p= fragCoord.xy / iResolution.xy;
  	float time=iTime*0.4;
   	float rnd=40.+floor(fract(time*time));// try to genereate random floor number !

	float a = 1.8;
	p.x = p.x*a;

	vec3 col = vec3(0.0);

    tx2.r = rnd+10.+tx2.b+sin(fract(iTime*2.)+uv.x/uv.y)*sin(iTime+tx2.g);


        float x, y, aa = 5.+time, b = 9.+time*0.8, ite = 20. ;

        for (float j = 0. ; j < 100. ; j++) {
                x =  0.8 * sin(aa * (ite / 7. + j / 40.)),
                y =  0.4 * sin(b * (ite / 7. + j / 40.));


         col += vec3(bob(p, vec2(0.9+x, 0.5 + y), j/1000., j));            

              }






   // (tx2.r>0.5) ? tx2.r=1.0: tx2.b=1.0;// i lowe this ternary conditions :)


	fragColor = vec4(vec3(col)/tx2, 1.0);
}
