// Color-Palettes Visualization - written 2018 by Jakob Thomsen
// (variant of https://www.shadertoy.com/view/XsdGR7)

#define draw(c,q) O = mix(vec4(c, 1.), O, vec4(smoothstep(.0,.02,abs(I.y - q))))

vec3 rainbow(float value, vec2 q) // smooth
{
    return pow(.5 - .5 * cos(1. * 3.1415926 * (value + vec3(0,1,2) * 2.0 * q.x)), vec3(exp2(2.0 * q.y - 1.0)));
}

vec3 rainbow2(float value, vec2 q) // smooth
{
    return exp(-pow(q.y * 2. * (-1.0 + value + vec3(0,1,2) * 2.0 * q.x), vec3(2.0)));
}

void mainImage( out vec4 O, in vec2 I )
{
    vec2 q = vec2(iMouse.xy) / iResolution.xy;
	I /= iResolution.xy;
    O = vec4(mix(rainbow(I.x, q), rainbow2(I.x, q), step(0.5, I.y)), 1.);
    //O = vec4(1);
    draw(vec3(1,0,0),.5*rainbow2(I.x, q).x-.5);
    draw(vec3(0,1,0),.5*rainbow2(I.x, q).y-.5);
    draw(vec3(0,0,1),.5*rainbow2(I.x, q).z-.5);
    draw(vec3(1,0,0),.5*rainbow(I.x, q).x);
    draw(vec3(0,1,0),.5*rainbow(I.x, q).y);
    draw(vec3(0,0,1),.5*rainbow(I.x, q).z);

    O = sqrt(O); // gamma correction
}
