void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p1 = vec2 (50., 50.);
    vec2 p2 = vec2 (90., 70.);

	float d1 = length (p1 - fragCoord) - 25.;
	float d2 = length (p2 - fragCoord) - 25.;

    float k = .2;
    float dm = -log2 (exp2 (-k*d1) + exp2 (-k*d2)) / k;

    vec4 colA = vec4 (-d1);
    vec4 colB = vec4 (-d2);
    vec4 colC = vec4 (-dm);    

    //fragColor = max (colA, colB);
    //fragColor = colC;
    fragColor = clamp (colC, 0., 1.) - clamp (max (colA, colB), 0., 1.) * .5;
}
