void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = 4.*(2. * fragCoord.xy - iResolution.xy)/iResolution.y;

	float a = atan(uv.x, uv.y) / 3.14159 * 1.5;
	float r = length(uv) - iTime*0.2;
	a+=r*1.*sin(iTime*0.05);
	uv = abs(fract(vec2(a+r,a-r))-0.5);

	float x = uv.x*1.248;
	float y = uv.y*6.;
	float z = uv.y*1.8;
	float hex =1.3-abs(y - x - 1.386);

	vec3 col = hex * 0.6 * vec3(0.15,0.24,0.37)/length(uv);

	fragColor.rgb = col;
}
