float dis_c(vec2 center, float r, vec2 coord){
    //Signed Distance Field Function;
    //Circle
    //vec2 offset = coord - center; 
    //float d = sqrt((offset.x * offset.x) + (offset.y * offset.y)) - r;
    float d = distance( center, coord ) - r;
    return d;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.x;
	//vec4 bg = vec4(uv,0.5+0.5*sin(iTime),1.0);
    vec4 bg = vec4(1.0,0.65,0.36, 1.0); //因为是横屏，所有水平是0-1，垂直可能是0-0.6;

    float dc1 = dis_c(vec2(0.5,0.22),0.18,uv);  //Method:center,a,b,fragCoord -->UV(0.0~1.0);
    vec4 shape1 = vec4(vec3(0.2,0.5,0.9),1.0 - smoothstep(-0.001,0.001,dc1)); //smoothstep(a,b,x);(a,b) range (-1.0~1.0);
    //shape1 = vec4(vec3(1.0,0.5,0.8),1.0 - clamp(de1,0.0,1.0));  //clamp;

    //stroke
    float stroke = 0.01; //stroke size
    vec4 strokeLayer = vec4(vec3(0.8,0.8,0.8),1.0-smoothstep(-0.001,0.001,dc1-stroke));//Expand a circle to stroke;

    shape1 = vec4(mix(strokeLayer.rgb, shape1.rgb, shape1.a), strokeLayer.a);
    //end stroke.

    vec4 layer1 = mix(bg,shape1,shape1.a);
    fragColor = layer1;

}
