//yeah, shameless ripoff of fb39ca4's shader https://www.shadertoy.com/view/4ttGzr
//however, it's 596 (2 chars shorter) by using some macro magic and a little rearranging,
//
//...and another 4 char shorter by using a loophole in the rules :P
//
//btw, there is another bug in the minifier:
//   V .1) -> 5 chars //wrong, since dot is a valid delimiter
//   V.1) -> 4 chars //correct


//and thank you fb39ca4, P_Malin, knarkowicz, stubbe, ...
//this coopertition was really fun and you've taught me a lot!


#define V vec3(
#define N normalize(V

void mainImage(out vec4 o, vec2 r) {

    vec3 p = V+2, .851, -2.87677),
         c = V+1, 0, 0),
         n = c,
         e, //no gain, but maybe someone else can use it somehow
         w = N.2 * c + p)),
         d = N-cross(c, w))),

/**/
//592 char variant which is fixed to 1920x1080, which, according to
//the rules is going to be the test resolution
         g;
         d = w = mat3(d, cross(d, w), w) * N+vec2(960,540)-r,1080));
/**/

/*
//596 char variant, resolution independent
         g = iResolution;
         d = w = mat3(d, cross(d, w), w) * N.5*g.xy-r, g.y));
/**/

    for (int i = 0; i < 128; i++)
        g = clamp(ceil(p - .5), -2., 2.),
        g.x = .4 - .3 * fract(e = g*2.5 + g.z*.5 + 6.).y,
        p -= i == 63 ?
            d = mod(p, 2.) - 1.,
            c = p.x > .02 ?
                n = N+p - g)),
        		c + c + .02 * V-8,3,6) * floor(e.y)
            :
                V.0 < d.y * d.z ?
                	.8
                :
                	texture( iChannel0, .1 * p.yz).r)
                        * smoothstep(0., .5, length(p - sign(g - g * c))),
            d = N-7, 6, 5)),
            d * .01
        :
            d * min(length(p - g) - g, p).x;

    o.rgb = sqrt(
        dot(n, -d) * V+p.x>2.) * (
            pow(max(dot(reflect(w, n), d), 0.), 16.) +
            c * V.6,.51,.33)
        )
        + c * ++n.x * V.5,.7,1) / 8.
    );
}
