float sd_circ(vec2 p, float r)
{
	return length(p) - r;

}

float opRep( vec2 p, vec2 c )
{
    vec2 q = mod(p,c)-0.5*c;
    return sd_circ( q, .01 * (2.0 * length(p)) );
}
float smooth(float f)
{
    return pow(f, 3.4);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / (iResolution.xy);

    uv -=.5;
    float actual_time = iTime * .7;

    // pow(mod(actual_time, 1.0), 3.0);
    float breath = actual_time - mod(actual_time, 1.0) + smooth(mod(actual_time, 1.0)); 
    breath*= 3.14/4.0;

    float wiggle_x = sin(breath +  abs(uv.x))*.1;
    float wiggle_y = cos(breath +  abs(uv.y))*.1;
    float sd = opRep(uv,vec2(.07) +  (sin(breath/2.0)*8.0 + 1.0)* .2 * vec2(wiggle_x, wiggle_y));
    fragColor = vec4(smoothstep ( 0.0, .01, sd));
}
