    vec3 f = fract(x);
	f = f*f*(3.-2.*f);

	vec2 u = (p.xy+vec2(37.0,17.0)*p.z) + f.xy;
	vec2 g = texture( iChannel0, (u+.5)/256., -100. ).yx;
