void mainImage(out vec4 O,vec2 X)
{
	vec2 k = iResolution.xy,
         u = (X + X - k) / k.y,
         w = u;

    X=vec2(1.23,.71);
    u.y = abs(u.y);
    u -= X*max(0.,dot(X,u));
    u.y = abs(u.y);

    X = clamp(abs(u - u.x - .9) - .1, u - .53, max(u - .07, abs(u.x + .43) - .43));

    O = vec4(.83,.87,.92,1) - clamp(X*.15*k,.0,.7).y + clamp(sin(iTime*4.+w.x+w)*.5-.4-X,.0,1.).y;
}
