float acc;//color accumulation per pixel
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 distort = texture(iChannel3, vec2(mod(uv.x + cos(iTime), 1.0), mod(uv.y + sin(iTime), 1.0))).rg;
    vec4 col = vec4(texture(iChannel0, uv + distort).rgb, 1.0);

    vec4 inverseCol = vec4(vec3(1.0 - col.xyz).xyz, 0.5);

	fragColor = col;
}
