void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    vec2 p = fragCoord.xy / iResolution.xy;
    p.x *= iResolution.x/iResolution.y;

    vec2 q = p - vec2(0.5 * iResolution.x/iResolution.y, 0.5);

    vec3 purple = vec3(0.6, 0.4, 0.7);
	vec3 white = vec3(1.0); 

    float r = 0.144;

    float mixVal = smoothstep( r, r+0.001, length(q));

    vec3 color = mix(white, purple ,mixVal);

    fragColor = vec4(color,1.0);



}
