void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    // reflect, scale
	uv=abs(2.0*(uv-0.5));
    uv *=0.2525; 

    // sample fft
    float x = uv.x; 
	float y=abs(texture( iChannel0, vec2(uv.x,0.25) ).x ); // 0.25 = fft

    // determine step
    //float a = uv.y*(iMouse.x/ iResolution.x);
    //float b = uv.x*(iMouse.y/ iResolution.y);
    float a = uv.y*0.15; 
    float b = uv.x*0.25; 
    float dx = a-b;

    // step along, multiplying..
    for (int i=1;i<4;i++) {
        x = x+dx;  // step
        y *= abs( texture( iChannel0, vec2(x,0.25) ).x );
    }

    y*=0.9;
    fragColor = vec4( sin(y*3.141*2.5), sin(y*3.141*2.0),sin(y*3.141*1.0),1.0);

}
