float rectDist(vec2 p, vec2 dim) 
{
    //return length(max(abs(p) - dim, 0.0));

    //return abs(p).x - dim.x*0.5;
    //return abs(p).y - dim.y*0.5;
    //return max(p.x, p.y);
    //return max(p.x - dim.x*0.5, p.y - dim.y*0.5);	// 不对称

    return max(abs(p).x - dim.x*0.5, abs(p).y - dim.y*0.5);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (2.*fragCoord.xy - iResolution.xy) / iResolution.xx;

    //float dist = rectDist(uv, vec2(abs(sin(iTime)), 0.0));
    //float dist = rectDist(uv, vec2(0.0, abs(sin(iTime))));

	//float dist = rectDist(uv + vec2(0., sin(iTime)), vec2(0.0, 0.0));
    float dist = rectDist(p + vec2(sin(iTime)*.5, cos(iTime)*.5), vec2(0.0, 0.0));
    //fragColor = vec4(dist);


    float thickness = 0.25;
    float shift = .1;
    float d = 1. - smoothstep(thickness, thickness + shift, dist);

    vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 color = texture(iChannel0, uv);

	fragColor = color * d;
}
