float draw(int z, vec2 v)
{
	switch(z)
	{
		case 0:
			return step(0.5, fract(sin(dot(v, vec2(6514.0,  918.0))) * 1000.0));
	    case 1:
	        return step(0.5, fract(sin(dot(v, vec2(  12.0,    0.0))) * 1000.0));
	    case 2:
			return step(0.5, fract(sin(dot(v, vec2(8342.0, 7692.0))) * 1000.0));
        case 3:
	        return step(0.5, fract(sin(dot(v, vec2( 791.0,  474.0))) * 1000.0));
        case 4:
	        return step(0.5, fract(sin(dot(v, vec2(1095.0, 9193.0))) * 1000.0));
        case 5:
	        return step(0.5, fract(sin(dot(v, vec2( 403.0, 2336.0))) * 1000.0));
        case 6:
	        return step(0.5, fract(sin(dot(v, vec2(9338.0, 3951.0))) * 1000.0));
        case 7:
	        return step(0.5, fract(sin(dot(v, vec2(1609.0, 4238.0))) * 1000.0));
        case 8:
	        return step(0.5, fract(sin(dot(v, vec2(1825.0,  104.0))) * 1000.0));
        case 9:
	        return step(0.5, fract(sin(dot(v, vec2( 713.0, 5789.0))) * 1000.0));
        default:
		    return 0.0;
	}
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    vec2 uv = gl_FragCoord.xy/iResolution.xy;
    uv *= 5.0;
    vec2 ipos = floor(uv);
	int t = int(iTime);
    vec3 color = vec3(draw(t, ipos));	
    fragColor = vec4(color, 1.0);
}
