{
    vec2 st = fragCoord/iResolution.xy;
    st=5.0*st-2.5;
    float sinusoid = sin(2.1*st.y+1.2*iTime); 
   	float sinusoid2 = sin(1.*(st.x*sin(iTime/2.)+st.y*cos(iTime/3.))+iTime);
    st.x = st.x +sin(iTime);
    st.y = st.y +cos(iTime);
    float sinusoid3 = sin(sqrt(3.*(pow(st.x,2.0)+pow(st.y,2.0))+1.0+iTime            ));
  	st.x = st.x +.1*sin(iTime/2.);
    st.y = st.y +1.10*cos(iTime/2.);
