void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2. * fragCoord - vec2(iResolution.x, 0.)) / iResolution.y * vec2(1.3, 1.);
    uv = abs(uv);
    for (float i = 0.; i < 4.; i++) {
        float a = i * 0.05;
        uv = mat2(cos(a), sin(a), -sin(a), cos(a)) * uv;
        uv = 1.5 * uv - smoothstep(0., 1., fract(iTime * 0.05) + 0.2) * 1.8 + 0.6;
        uv = vec2(atan(uv.y, uv.x), length(uv));
        uv.x -= 1. / uv.y * 1.5;
        uv.y = 1. / pow(abs(uv.y), 1.2);
        uv = vec2(cos(uv.x), sin(uv.x)) * uv.y;
        uv = abs(uv);
    }
    float d = length(uv - vec2(0.5, 0.4)) - 0.4;
    fragColor = vec4(d);
}
