float random (in vec2 _st) {
    return fract(sin(dot(_st.xy,
                         vec2(12.9898,78.233)))*
        43758.5453123);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 st = (fragCoord.xy - .5 * iResolution.xy) / iResolution.y;


    /*
	float f = 0.;
    st.x *= 20.;
    st.x = step(fract(st.x), 0.5);
    st.x += step(fract(st.y), 0.5);
    f = mod(st.x, 2.) == 0. ? 0. : 1.;
    */

	st.y *= 20.;
    st.x *= 100.;

    float t = iTime * (ceil(st.y) -.5) * random(ceil(st)) * 10.;
    st.x += t;

    vec2 a = floor(st);

    float f = random(a);

    f = step(.3, f);


    vec3 color = vec3(f);
    fragColor = vec4(color,1.0);
}
