const float PI	 	= 3.14159265359;
const float FREQ 	= 640.0;

//Lyman series
float series1(int n, float time)
{
    float N=float(n*n);
    return sin((1.0-1.0/N)*time*2.0*PI*FREQ);
}

//Balmer series
float series2(int n, float time)
{
    float N=float((n+1)*(n+1));
    return sin((0.25-1.0/N)*time*2.0*PI*FREQ);
}

//Paschen series
float series3(int n, float time)
{
    float N=float((n+2)*(n+2));
    return sin((1.0/9.0-1.0/N)*time*2.0*PI*FREQ);
}

float series(float time)
{
    float result=0.0;
    for(int i=1; i<10;++i)
    {
        result+=series1(i,time);
        result+=series2(i,time);
        result+=series3(i,time);
    }
    return result;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
