    float r=1.0-dot(uv, uv)+v;
    fragColor = vec4(r,r,r ,1.0);
}

#define TAU 6.283185307179586476925286766559
const float FREQ 	= 440.0;

float series(int ni, int n, float time)
{
    float N=float(n*n);
    float NI=float(ni*ni);
    return sin((1.0/NI-1.0/N)*time*TAU*FREQ);
}



float series(float time)
{
    float result=0.0;
    for(int i=2; i<10;++i)
    {
        result+=series(1,i,time);//Lyman series
    }
    for(int i=3; i<10;++i)
    {
        result+=series(2,i,time);//Balmer series
    }
    for(int i=4; i<10;++i)
    {
        result+=series(3,i,time);//Paschen series
    }
    return result;
}

float fun(float time, float nm, int I)
{
    float highest_relative_intensity = 840000.0;
    float amp = sqrt(float(I)/highest_relative_intensity);
    float base = FREQ;
    float bass = 40.0;
    float peak0 = 91.175;
    float peak1 = 656.0;
    float f = ((1.0/nm)*(bass - base)*peak0*peak1+peak0*base-peak1*bass)/(peak0-peak1);
    //amp = 1.0;
    return sin(f*time*TAU)*amp;
}

