float line(vec2 a, vec2 b, vec2 c)
{
    vec2 abn = normalize(b-a);
    float t = dot(abn, c-a);
    vec2 d = t * abn;
    float l = length((c-a)-d);

    float n = 0.0;
    if (t > 0.0 && t < length(b - a))
    	n = -log(l);
    return n;
}

vec2 a = vec2(155.0,160.0);
vec2 b = vec2(150.0,150.0);
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    a.x = iMouse.x;
    a.y = iMouse.y;

    vec2 c = fragCoord.xy + 0.5;

    float n = line(a, b, fragCoord.xy + 0.5);

    fragColor = vec4(n, n, n, 1.0);
}
