float rect(vec2 uv, vec2 pos, vec2 size)
{
    float ratio = iResolution.x/ iResolution.y;
    uv.x *= ratio;
    //size.x *= ratio;
    pos.x *= ratio;

    float value = step(pos.x,uv.x);
    value -= step(pos.x + size.x,uv.x);  
  	value -= step(pos.y + size.y ,uv.y);
    value -= 1.0 -step(pos.y,uv.y);

    return value;
}

vec3 white = vec3(1,1,1);
vec3 blue = vec3(0,0,1);
vec3 red = vec3(1,0,0);
vec3 yellow = vec3( 1,1,0);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;

	vec2 pos = vec2(.5,.5);
   	vec2 size = vec2(.2,.2);


    float value;
    vec3 color;
    value = rect(uv, vec2(.0,.9), vec2(.5,.1));
	color = mix(color, red ,value);
    value = rect(uv,vec2(.0,.65),vec2(.5,.2));
	color = mix(color,white,value);
    value = rect(uv,vec2(.0,.1),vec2(.5,.5));
	color = mix(color,blue,value);
    value = rect(uv,vec2(.0,.0),vec2(.5,0.05));
	color = mix(color,white,value);

    value = rect(uv,vec2(.3,.9),vec2(.1,.1));
	color = mix(color,white,value);
    value = rect(uv,vec2(.3,.65),vec2(.1,.2));
	color = mix(color,yellow,value);
    value = rect(uv,vec2(.3,.1),vec2(.1,.5));
	color = mix(color,white,value);
    value = rect(uv,vec2(.3,.0),vec2(.1,.05));
	color = mix(color,red,value);

    value = rect(uv,vec2(.38,.9),vec2(.3,.4));
	color = mix(color,white,value);
    value = rect(uv,vec2(.38,.65),vec2(.3,.2));
	color = mix(color,red,value);
    value = rect(uv,vec2(.38,.1),vec2(.3,.5));
	color = mix(color,white,value);
    value = rect(uv,vec2(.38,.0),vec2(.3,.05));
	color = mix(color,blue,value);

    value = rect(uv,vec2(.57,.9),vec2(.4,.4));
	color = mix(color,white,value);
    value = rect(uv,vec2(.57,.65),vec2(.4,.2));
	color = mix(color,red,value);
    value = rect(uv,vec2(.57,.1),vec2(.4,.5));
	color = mix(color,white,value);
    value = rect(uv,vec2(.57,.0),vec2(.4,.05));
	color = mix(color,blue,value);






    fragColor = vec4(color,1.0);

}
