void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 res = iResolution.xy;
	vec2 uv = fragCoord.xy ;
    vec2 tc = uv / res;


    float d = distance(vec2(0.5 * (res.x/res.y), 0.5),vec2(tc.x * (res.x/res.y), tc.y));

	fragColor = texture(iChannel0, vec2(d+sin(iTime*0.2)));
}
