float hash(vec2 p)
{
    p  = 20.0*fract( p*0.3 + vec2(0.9,0.101));

    return -1.0 + 2.0 * fract((p.x + p.y) * p.y * p.x);
}

float noise( in vec2 p )
{
    vec2 i = floor( p );
    vec2 f = fract( p );

	vec2 u = f*f*(3.0-2.0*f);

    return mix( mix( hash( i + vec2(0.0,0.0) ), 
                     hash( i + vec2(1.0,0.0) ), u.x),
                mix( hash( i + vec2(0.0,1.0) ), 
                     hash( i + vec2(1.0,1.0) ), u.x), u.y);
}
float fbm( in vec2 p) {
    float c = 0.0;

    for (float i=1.0;i<10.0;i++) {
        c+=noise(p*pow(1.5, i))*0.9*pow(0.6, i);
    }
    return c;
}

float c(in vec2 p,in float x){

p += iTime*0.01;

return fbm(p)*x;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = fragCoord.xy / iResolution.xy;

    p.xy += iTime*0.01;

    vec4 clouds = vec4(1.0);

    clouds *= c(p*5.0,fbm(p*5.0));

	fragColor = clouds;
}
