//anastadunbar's (Original)

#define PI 3.1415926535897932384
#define SIZE 0.08

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float time = iTime;
	vec2 uv = (fragCoord.xy / iResolution.xy);
    vec2 uva = ((uv-0.5)/vec2(1.,iResolution.x/iResolution.y)+0.5);
    float a = clamp(1.-(pow(clamp(mod((atan((uva.x)-0.5,(uva.y)-0.5)-(time*2.6))/(PI*2.),1.), 0., 1.),3.)),0.,1.);
    float b1 = 1.-clamp((length(vec2(uva.x,uva.y)-0.5)-(SIZE))*260.0, 0., 1.);
    float b2 = 1.-clamp((length(vec2(uva.x,uva.y)-0.5)-(SIZE-0.03))*260.0, 0., 1.);
    float b12 = clamp(b1-b2, 0., 1.);
    float finish = clamp(b12-a, 0., 1.);
	fragColor = vec4(vec3(finish),1.0);
}

