void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float twoPi = 6.283185307179586;

    // base coords with "carrier wave"
	vec2 uv = -5.0 * tan(twoPi + iTime * 0.1) * sin(twoPi + iTime * 1.0) * fragCoord.xy / iResolution.xy;

    // refract
    uv.x = uv.x + 0.1 * sin(uv.y * twoPi + iTime * 1.0);
    uv.y = uv.y + 0.1 * sin(uv.x * twoPi + iTime * 1.0);
    //scroll
    uv.y = -uv.y - iTime * 1.0;
    uv.x = -uv.x - iTime * 1.0;

    // texture lookup
    vec4 color = texture( iChannel0, uv );

    // monochrome
    float grey = (color.r + color.b + color.g) / 32.0;

    // look up from ramp
    fragColor = texture( iChannel1, vec2(grey, iTime / 300.0));
}
