float len2(vec2 v) {
    return pow(length(v), 2.);
}

float dist(vec2 xy) {
    float t = iTime * 1.;
    return pow(xy.x-t*(1.-xy.x/len2(xy)), 2.) - (t*xy.y)/len2(xy) - xy.y;
    //return pow(xy.x - cos(t) * length(xy), 2.) - (xy.y - sin(t) * length(xy));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord - 0.5*iResolution.xy)/iResolution.y;
    float c = dist(uv);
    c = c <= 0. ? 1. : 0.;
	fragColor = vec4(c, 0., c, 1.0);
}
