float isInBand(vec2 uv, float frequency, float amplitude) {
    float waveValue = amplitude * sin(uv.x + iTime + frequency);
    float lineWidth = 0.05;
    return step(1.0 - lineWidth, 1.0 - abs((uv.y * 4.0 - 2.0) - waveValue));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2  uv              = fragCoord.xy / iResolution.xy;
    vec3  backgroundColor = vec3(1.0, 1.0, 0.85);
    vec3  blockColor      = vec3(0.2, 0.2, 0.15);
    vec3  bandColor       = vec3(1.0, 0.1, 0.1);
    float isXBlock        = step(1.0 / 80.0, mod(uv.x, 1.0 / 40.0));
    float isYBlock        = step(1.0 / 140.0, mod(uv.y, 1.0 / 40.0));
    float isBlock         = isXBlock * isYBlock;
    float isBand          = 0.0;

    for(float i = 0.0; i < 8.0; i++)
      isBand += isInBand(uv, 2.0, i / 6.0);

    vec3 finalBackground = mix(backgroundColor, blockColor, isBlock); 
    vec3 finalColor      = mix(finalBackground, bandColor, isBand);

	fragColor = vec4(finalColor,1.0);
}
