void mainImage (out vec4 kleur, in vec2 doos2)
{ 
    vec2 uv = doos2.xy / iResolution.xy ;
    float ratio = iResolution.y/iResolution.x ;
    kleur = vec4 (0.6,uv.y*0.8,uv.y,1) ; 
	if (uv.y <0.5+cos(uv.x*2.0+iTime)*0.01)
    { 
        kleur = vec4 (0.0,uv.y,0.5,1) ;
    }
    vec2 balpositie = vec2(0.5,0.5+cos(iTime*1.5)/25.0);
    float h = (uv.x-balpositie.x)/ratio;
    float v = uv.y-balpositie.y;
    float afstand = sqrt(pow(h,2.0) + pow(v,2.0));

    if (afstand < 0.2) 
    { 
        kleur = vec4 (uv.y*1.6,uv.y*0.6,uv.x,1) ;
    }

    kleur = vec4(kleur.x - afstand/1.7, kleur.y - afstand/1.3, kleur.z - afstand/1.7, kleur.w) ;
}
