void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = texture(iChannel0, uv);
}

//replaced http://byteblacksmith.com/improvements-to-the-canonical-one-liner-glsl-rand-for-opengl-es-2-0/
//with FerbriceNeyret2's version.
#define rand(v) fract( sin( dot( v, vec2(12.9898,78.233 )) ) * 43758.5453 )

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = fragCoord.xy / iResolution.xy;
  if(iTime < 2.0){
    //thanks to FerbriceNeyret2 for posting a shorter version.
    fragColor = step(.5, vec4( fract(uv.x*2.), uv, 1) );
  }else{
    vec3 d = vec3(1.0 / iResolution.xy, 0.0);
    vec2[8] dchoices = vec2[8](d.xz, d.zy, -d.xz, -d.zy, d.xy, -d.xy, d.xz-d.zy, d.zy-d.xz);
    int r = int(rand(fragCoord+iTime)*8.0);
    fragColor = texture(iChannel0, uv+dchoices[r]);
  }
}
