#define TWOPI 6.283185

vec3 palette(in float t)
{
    // https://iquilezles.org/articles/palettes
	vec3 a = vec3(0.5);
    vec3 b = vec3(0.5);
    vec3 c = vec3(1.0);
    vec3 d = vec3(0.0, 0.33, 0.67);
    return a + b * cos(TWOPI*(c * t + d));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy * vec2(1.75, 0.5) + vec2(0.0, 0.2);
    float m = 200.0;
    float ts = abs(cos(iTime / 5.0));
    float k = 100.0 * ts;
    vec2 pq = vec2(-0.74543, 0.11301);
    vec2 dmin = vec2(-1.8);
    vec2 dmax = vec2(1.8);
    vec2 dv = (dmax - dmin) / (pq - vec2(1.0));
    vec2 dk = dmin - uv * dv;
    float rv = 0.0;
    for(float s=0.0; s<k; s+=1.0){
        dk = vec2(
        	dk.x * dk.x - dk.y * dk.y + pq.x,
        	2.0 * dk.x * dk.y + pq.y
    	);
        float rr = dk.x * dk.x + dk.y + dk.y;
        if(rr > m){
        	rv = s/k;
            break;
        }
    }
    if (rv == 0.0) {
    	rv = 1.0;
    }
    vec3 col = palette(rv);
    col = pow(col, vec3(1.0/2.2));
	fragColor = vec4(col,1.0);
}
