void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;


    vec3 colorA = vec3(1.0, 1.0, 0.0);
    vec3 colorB = vec3(0.0, 0.0, 0.0);
    float sunX = abs(uv.x - 0.8);
    float sunY = abs(uv.y - 0.7);

    float distan = sqrt(sunX*sunX + sunY*sunY);

    vec3 color = mix(colorA,colorB,abs(sin(distan)));



    fragColor = vec4(color,1.0);

}
