const float pi = 3.141592654;
const vec4 blue = vec4(0.0, 0.5, 0.8, 0.0);
const vec4 black = vec4(0.0);

const float thickness = 0.15;
const float n = 15.0;
const float m1 = 1.0;
const float a = 0.25;
const float t1 = 2.5;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy - 0.5 * iResolution.xy) / iResolution.yy;
	float r = length(uv);
    float theta = atan(uv.y, uv.x);
    float r1 = floor(r * n + 0.5);
    float m = m1 * r1;
    float x = n * r + a * sin (m * theta + t1 * iTime);
    fragColor = fract(x) < thickness ? blue : black;
}
