void mainImage( out vec4 f, in vec2 g )
{
    f = vec4(.8,.2,.5,1);
    vec2 s = iResolution.xy;
	vec4 h = texture(iChannel0, g / s);
   	g = (g+g-s)/s.y*1.3;
    vec2 
        k = vec2(1.6,0) + mod(iDate.w,6.28), 
        a = g - sin(k),
    	b = g - sin(2.09 + k),
    	c = g - sin(4.18 + k);
    f = (0.2/max(abs(a.x)+a.y,-a.y) // tri
         + 0.2/max(abs(b.x), abs(b.y)) // quad
         + 0.2/max(max(abs(c.y - c.x), abs(c.y + c.x)), abs(c.x))) // losange
        * 0.04 + h * 0.95 + step(h, f) * 0.05;
}
