void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float tentacleWeight = 25.0;

	vec2 uv = floor(fragCoord.xy / tentacleWeight) * tentacleWeight / iResolution.xy;
    vec2 cthulhu = abs(sin(uv + iTime));
    float clr = cthulhu.x * cthulhu.y;
	fragColor = vec4(1.0, clr, clr, 1.0);
}
