#define t iTime
#define r iResolution.xy

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
	vec3 c;
	float l,z=t;
    for(int i=0;i<3;i++){
		vec2 uv,p=fragCoord.xy/r;
		uv=p;
		p-=.5;
		p.x*=r.x/r.y;
		z+=1.;
        float fi = z;

    	mat2 rotate = mat2(cos(z),-sin(z),sin(z),cos(z)); 
    	p=rotate*p;
		l=length(p);

    	uv=p/l*abs(sin(l*5.-z*1.5))*(sin(z)*0.5+2.);
		l=length(uv);
		c[i]=.1/length(abs(mod(uv,1.))-.5);
    }

	fragColor=vec4(c*smoothstep(0.1,1.0,t),t);
}
