void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec2 uv2 = uv;

    uv.x = floor(uv.x * 25.0);
    uv.x = uv.x / 25.0;
    uv.y = floor(uv.y * 25.0);
    uv.y = uv.y / 25.0;

    uv.x += 0.02;
    uv.y += 0.02;

    float dis = distance(uv2, uv);
    if ((dis * 50.0) < 1.0)
    {
        fragColor = texture(iChannel0, uv);
    }
	else
    {
        fragColor = texture(iChannel0, uv2);
        fragColor *= 0.5;
    }
}
