float createWave (in float dist, vec2 pixCoords){
    float amplitude = 0.1 / dist * 2.0;
    float vitesse = 10.0 / dist;
    float periode = 50.0 / dist;
    float hOeil   = 0.2;
    float Deg2Rad = 3.14 / 180.0;
    float eyeToScreenDistance = 5.0;
    float yOffset = hOeil + eyeToScreenDistance * (hOeil / 130.0 *  dist);// + (hOeil / 1.5 - hOeil) /  dist + dist; 



	return iResolution.y * (amplitude * cos((2.0 / periode)  * Deg2Rad * pixCoords.x + vitesse * iTime) + yOffset);
}

void mainImage (out vec4 color, in vec2 pixCoords){
    float rotationX = pixCoords.x * cos(iTime * 50.0) + pixCoords.y * sin(iTime * 50.0);
    float rotationY = pixCoords.x * sin(iTime) + pixCoords.y * (-cos(iTime));

    pixCoords.x += rotationX;
    //pixCoords.y -= rotationY;

    //Création Ciel

    color = vec4(0.5, 0.7, 1.0, 1.0);

    //Création Soleil
    /*iChannel0;
    float rayon = 70.0;
    float distance = sqrt(pow(iResolution.x / 1.10 - pixCoords.x, 2.0) + pow(iResolution.y / 1.20 - pixCoords.y, 2.0));
    if ( distance <= rayon){
      	color = vec4 (0.942 - iTime / 1000.0, 0.621 - iTime / 1000.0, 0.093 - iTime / 1000.0, 1.0);

   	}*/


    //Création Mer


    const float plans = 60.0;
    for (float dist = plans; dist >= 2.0; -- dist){
    	if (pixCoords.y <= createWave(dist, pixCoords)){
        	color = vec4(0.0, 0.1 * (dist / 7.0), (0.3 * (dist / 9.0)) + 0.3, 1.0 - (dist / 10.0));
        }
    }



}
