// --- animated variant of https://www.shadertoy.com/view/ltKSRG

#define S(v)   O += smoothstep(.04, .025, v) 

void mainImage( out vec4 O, vec2 U )
{
	U = (U+U -(O.xy=iResolution.xy)) / O.y;
    mat2 R = mat2(.9,-.5,.5,.9);
	O = vec4(1,0,0,1);

    S( abs(length(vec2(U.x, abs(U.y)+.7))   -1.));      // body

    U.x = abs(U.x);                                     // horizontal symmetry
    float t = iDate.w,
          l = length( U- vec2(.2-.02*sin(t), .55) );

    U.y>.3 && U.y<.45 ? S( abs(U.x*.55-U.y*.2)*1.5  )   // eyes
                      : S(sin(-t)>.997?1.:l) , S(abs(l-.1));

#define C(a,b,f,d)  U.x>a && U.x<b ? S( abs( .08*smoothstep(a,b,U.x)*sin(13.*U.x+f*1.57-t) -U.y + d) ) : O ; U *= R;
    C( .7, 1.4 , 2., .0  );                             // 3 tentacles
    C( .57,1.25, 3., .2  );
    C( .4, 1.  , 0., .25 );
}
