#define saturate(x) clamp(x,0.,1.)
float char(vec2 p,float c){//Thanks to Fabrice Neyret for this function
	if(p.x<0.||p.x>1.||p.y<0.||p.y>1.)return 1e5;
	return texture(iChannel0,p/16.+fract(floor(vec2(c,16.-c/16.))/16.)).a;
}
float forcedSigmoid(float x, float a){
	return(x>.5?2.-pow((-2.*x)+2.,a):pow(x*2.,a))/2.;
}
void mainImage(out vec4 fragColor,in vec2 fragCoord){
	vec2 uv=(fragCoord.xy/iResolution.xy)-.5;
	uv.x*=iResolution.x/iResolution.y;
	uv*=4.;
	float time=forcedSigmoid(fract(iTime),4.);
	float weight=.5;
	float sharpen=100.;
	float drawing=1.;
	//"Hello" => 72,101,108,108,111
	#define charMorph(p,aChar,bChar) 1.-saturate(((mix(char(p+.5,aChar),char(p+.5,bChar),fract(time)))-weight)*sharpen)
	drawing-=1.-saturate((char((uv-vec2(-3.+fract(time),0.))+.5,72.)-(weight*fract(time)))*sharpen); //Fading-in H - 72
	drawing-=charMorph(uv-vec2(-2.+fract(time),0.), 72.,101.); //H - 72
	drawing-=charMorph(uv-vec2(-1.+fract(time),0.),101.,108.); //e - 101
	drawing-=charMorph(uv-vec2( 0.+fract(time),0.),108.,108.); //l - 108 (Why morph into the same character?)
	drawing-=charMorph(uv-vec2( 1.+fract(time),0.),108.,111.); //l - 108
	drawing-=1.-saturate((char((uv-vec2( 2.+fract(time),0.))+.5,111.)-(weight*(1.-fract(time))))*sharpen); //Fading-out o - 111
	fragColor=vec4(vec3(drawing),1.);
}
