const float RADIUS = 1.;
const float BOKEH = 3.0;

float wvalue;

vec4 blur(sampler2D in_texture, vec2 in_Texcoord){
    vec4 Texture = texture(in_texture, in_Texcoord);    
    if(BOKEH != 0.0){
        float w = pow(abs(dot(Texture.rgb,vec3(0.333)) + length(Texture.rgb) + 0.1), BOKEH) + 1.0;
        wvalue += w;
        Texture *= w;
    }else{
        wvalue = 9.0;
    }
    return Texture;
}

void mainImage(out vec4 fragColour, in vec2 fragCoord)
{
   	vec2 uv = fragCoord/iResolution.xy;
    vec4 Texture = texture(iChannel0, uv);
    vec4 mask = texture(iChannel1, uv);
	float av = (mask.r+mask.g+mask.b)/3.;
    vec3 RESINV = texture(iChannel1, uv).rgb;
    float value = av;//dot(mask.rgb, vec3(0.0001));
    if(RESINV.z != 0.0) value = 1.0 - value;

    if(value != 0.0){
        vec2 offset, radius;
        float angle;

        radius = RADIUS * value / RESINV.xy;

        Texture = blur(iChannel0, uv);
        for (int i = 0; i < 8; i += 1){
            angle = 0.785398 * float(i);
            offset = uv + (vec2(cos(angle), sin(angle)) * radius)/iResolution.xy;
            Texture += blur( iChannel0, offset);
        }

        Texture /= wvalue;
    } else {
     	Texture = texture(iChannel0, uv);   
    }
    Texture.a = 1.0;

    fragColour = Texture;
}
