// based on oil move https://www.shadertoy.com/view/MtGcD3
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p=(2.0*fragCoord.xy-iResolution.xy)/max(iResolution.x,iResolution.y);
	for(int i=1;i<10;i++)
	{
		vec2 newp=p;
		newp.x+=0.6/float(i)*sin(float(i)*p.y+iTime+0.3*float(i))+1.0;
		newp.y+=0.6/float(i)*sin(float(i)*p.x+iTime+0.3*float(i+10))-1.4;
		p=newp;
	}
	vec3 col=vec3(1.0,1.0-(sin(p.y)),sin(p.x+p.y));
	fragColor=vec4(col, 1.0);
}
