mat2 rot(float a){
    return mat2(cos(a), -sin(a),
                sin(a), -cos(a));
}

void pMod1(inout float p, float size) {
	float halfsize = size*.5;
	p = mod(p + halfsize, size) - halfsize;
}

void pMod3(inout vec3 p, vec3 size) {
	p = mod(p + size*0.5, size) - size*0.5;
}

float sphere(vec3 p, float radius){
    return length(p)-radius;

}


float map(vec3 p)
{
    vec3 q = p;

    pMod3(q, vec3(.75, .6, .15));
    pMod3(q, vec3(0.9, 1., 0.6));

    pMod1(p.x, 1.);
    	q.y = abs(sin(iTime))-0.1;

    float s1 = sphere(p, .65); 
    float s2 = sphere(q, .5);
    float s3 = sphere(q, 1.);

    float disp = min(0.5 * (sin(p.x/3.) *
                       sin(p.y) *
                       (sin(p.z*10.)) ), 50.);
    	s1 -= disp;
    	s2 *= disp;
    	s3 *= disp;
    	//s1 -= disp;



  	float df1 = min(s1, s2); // Union
    float df2 = max(s2, s1); // Intersection
    float df3 = max(max(s1, s3), min(s1, s2)); // Difference

    return df3;
}


float trace(vec3 origin, vec3 r) 
{
  float t = 0.0;
    for (int i = 0; i < 64; ++i) {
        vec3 p = origin + r * t;
        float d = map(p);
        t += d*0.3;
        }
    return t;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec3 color = vec3((sin(iTime) * .3), -abs(cos(iTime))* .25, .5);
    uv = uv *2.-1.; // Remap the space to -1. to 1.
    uv.x *= iResolution.x/iResolution.y;


   	float FOV = 1.0;
   	vec3 ray = normalize(vec3(uv, FOV));

    ray.xy *= rot(iTime);
    //ray.yz *= abs(sin(iTime));


    vec3 origin = vec3(iTime, sin(iTime)*.1, -1.3);
    float t = trace(origin, ray);

    float expFog = sin(iTime) / (t* t* 0.4);

    vec3 fc = vec3(expFog);


    //fragColor = vec4((fc+color),1.0);
    fragColor = vec4(tan(fc+color),sin(iTime)*.4);
}
