void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float distortionScale = -0.04;
    float speed = 0.6;

    vec2 uv = fragCoord/iResolution.xy;
   	vec2 uv2 = vec2(uv.x, uv.y * distortionScale);

    float scale = iTime * distortionScale * speed;

    vec2 uvOffset = vec2(0.00, 0.03);//rand(1.0), rand(1.0));

    //float noiseA = texture(iChannel0, vec2(uv.x, uv.y + uvOffset + scale)).r;
    //float noiseB = texture(iChannel2, vec2(uv.x, uv.y + uv2 + scale)).r;
    float noiseA = texture(iChannel0, vec2(uv.x *0.4 + 0.02 * iTime, uv.y*0.3  + -0.4*iTime)).r;
    float noiseB = texture(iChannel2, vec2(uv.x *0.5 + -0.02 * iTime, uv.y *0.4 + -0.5*iTime)).r;
    float distortionFactor = 3.0;
    float distortion = distortionFactor * (noiseA * noiseB)* 0.7;

    float s =  (1.0 - uv.y) * distortion * texture(iChannel1, uv).g;

    vec2 newCoord = vec2(uv.x, uv.y + clamp(s, 0.0, 1.0));

    vec4 white = vec4(1.0, 1.0, 1.0, 1.0);
    vec4 red = vec4(0.9, 0.0, 0.0, 1.0);
    vec4 orange = vec4(0.8, 0.7, 0.0, 1.0);
    vec4 inner = orange * texture(iChannel3, newCoord).r;
    vec4 outer = red * (texture(iChannel3, newCoord).g * 1.0 - texture(iChannel1, newCoord).r);
    vec4 glow = white + texture(iChannel1, newCoord).b;
    //vec4 glow = white * texture(iChannel1, newCoord).;


    fragColor = vec4(outer.xyz + inner.xyz, 0.0);
    // fragColor.w = 0.1;
}
